import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const Navbar = ({ currentPage, navigateTo }) => {
  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
      <div className="container">
        <ul className="navbar-nav">
          <li className={`nav-item ${currentPage === 'home' ? 'active' : ''}`}>
            <a href="#" className="nav-link" onClick={() => navigateTo('home')}>
              Home
            </a>
          </li>
          <li className={`nav-item ${currentPage === 'about' ? 'active' : ''}`}>
            <a href="#" className="nav-link" onClick={() => navigateTo('about')}>
              About
            </a>
          </li>
          <li className={`nav-item ${currentPage === 'services' ? 'active' : ''}`}>
            <a href="#" className="nav-link" onClick={() => navigateTo('services')}>
              Services
            </a>
          </li>
          <li className={`nav-item ${currentPage === 'contact' ? 'active' : ''}`}>
            <a href="#" className="nav-link" onClick={() => navigateTo('contact')}>
              Contact
            </a>
          </li>
        </ul>
      </div>
    </nav>
  );
};

const Content = ({ currentPage }) => {
  return (
    <div className="container mt-4">
      {currentPage === 'home' && (
        <>
          <h1>Welcome to My Website</h1>
          <p>This is the main content of the home page.</p>
        </>
      )}
      {currentPage === 'about' && (
        <>
          <h1>About</h1>
          <p>This is the about page.</p>
        </>
      )}
      {currentPage === 'services' && (
        <>
          <h1>Services</h1>
          <p>These are the services we offer.</p>
        </>
      )}
      {currentPage === 'contact' && (
        <>
          <h1>Contact</h1>
          <p>Contact us for any inquiries.</p>
        </>
      )}
    </div>
  );
};

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');

  const navigateTo = (page) => {
    setCurrentPage(page);
  };

  return (
    <>
      <Navbar currentPage={currentPage} navigateTo={navigateTo} />
      <Content currentPage={currentPage} />
    </>
  );
};

export default App;
