import React, { useEffect, useState } from 'react';
import { Table } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUndoAlt } from '@fortawesome/free-solid-svg-icons';
import './Configuration.css'; // Import the CSS file for custom styling

const SystemControl = () => {
  const [eyeCareEnabled, setEyeCareEnabled] = useState(false);
  const [wakeOnLANEnabled, setWakeOnLANEnabled] = useState(false);
  const [socket, setSocket] = useState(null); 

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:4000'); 
    setSocket(ws);

    return () => {
      ws.close();
    };
  }, []);

  const sendEyeCareData = (newEyeCare) => {
    if (socket) {
      const data = {
        type: 'eyecare',
        value: !newEyeCare,
      };
      socket.send(JSON.stringify(data));
    }
  };

  const sendWakeonLanData = (newWakeonLan) => {
    if (socket) {
      const data = {
        type: 'wakeonlan',
        value: !newWakeonLan,
      };
      socket.send(JSON.stringify(data));
    }
  };

  const handleEyeCareToggle = () => {
    setEyeCareEnabled(!eyeCareEnabled);
    sendEyeCareData(eyeCareEnabled)
  };

  const handleWakeOnLANToggle = () => {
    setWakeOnLANEnabled(!wakeOnLANEnabled);
    sendWakeonLanData(wakeOnLANEnabled)
  };
  return (
    <div className="App">
      <Table className="table-dark">
        <tbody>
          <tr>
            <td className="Attribute">
              <div>
                <h4 className='title'>Set Unknown Sources</h4>
              </div>
              <div className="Attribute_Holder">
                <input
                  id="eyeCareCheckbox"
                  type="checkbox"
                  className="switch"
                  defaultChecked={eyeCareEnabled}
                  onChange={handleEyeCareToggle}
                />
                <label htmlFor="eyeCareCheckbox" />
              </div>
            </td>
            <td className="Attribute" >
              <div>
                <h4 className='title'>Reboot</h4>
              </div>
              <div className="Attribute_Holder">
                <input
                  id="muteCheckbox"
                  type="checkbox"
                  style={{ display: 'none' }}
                />
                <FontAwesomeIcon icon={faUndoAlt} />
              </div>
            </td>
            <td className="Attribute" >
              <div>
                <h4 className='title'>Power Off</h4>
              </div>
              <div className="Attribute_Holder">
                <input
                  id="muteCheckbox"
                  type="checkbox"
                  style={{ display: 'none' }}
                />
                <FontAwesomeIcon icon={faPowerOff} />
              </div>
            </td>
          </tr>
        </tbody>
      </Table>
    </div>  
  )
}

export default SystemControl;
