import React, { useEffect, useState } from 'react';
import { Table } from 'react-bootstrap';

const DeviceControl = () => {
  const [backLightEnabled, setbackLightEnabled] = useState(false);
  const [wakeOnLANEnabled, setWakeOnLANEnabled] = useState(false);
  const [socket, setSocket] = useState(null); 

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:4000'); 
    setSocket(ws);

    return () => {
      ws.close();
    };
  }, []);

  const sendBackLightData = (newBackLight) => {
    if (socket) {
      const data = {
        type: 'backlight',
        value: !newBackLight,
      };
      socket.send(JSON.stringify(data));
    }
  };

  const sendWakeOnLANData = (newWakeOnLAN) => {
    if (socket) {
      const data = {
        type: 'wakeonlan',
        value: !newWakeOnLAN,
      };
      socket.send(JSON.stringify(data));
    }
  };

  const handleBackLightToggle = () => {
    const newBackLightEnabled = !backLightEnabled;
    setbackLightEnabled(newBackLightEnabled);
    sendBackLightData(backLightEnabled);
  };

  const handleWakeOnLANToggle = () => {
    const newWakeOnLANEnabled = !wakeOnLANEnabled;
    setWakeOnLANEnabled(newWakeOnLANEnabled);
    sendWakeOnLANData(newWakeOnLANEnabled);
  };

  return (
    <div className="App">
      <Table striped variant="dark">
        <tbody>
          <tr>
            <td className="Attribute">
              <div>
                <h4 className='title'>Screen Backlight</h4>
              </div>
              <div className="Attribute_Holder">
                <input
                  id="eyeCareCheckbox"
                  type="checkbox"
                  className="switch"
                  checked={backLightEnabled}
                  onChange={handleBackLightToggle}
                />
                <label htmlFor="eyeCareCheckbox" />
              </div>
            </td>
            <td className="Attribute" >
              <div>
                <h4 className='title'>Wake-on-LAN</h4>
              </div>
              <div className="Attribute_Holder">
                <input
                  id="wakeOnLANCheckbox"
                  type="checkbox"
                  className="switch"
                  checked={wakeOnLANEnabled}
                  onChange={handleWakeOnLANToggle}
                />
                <label htmlFor="wakeOnLANCheckbox" />
              </div>
            </td>
          </tr>
        </tbody>
      </Table>
    </div>
  );
}

export default DeviceControl;
