import React, { useState } from 'react'
import './DeviceControl.css';
import { Table } from 'react-bootstrap';
const PictureControl = () => {
  const [backLight, setbackLight] = useState(50);
  const [brightness, setBrightness] = useState(50);
  const [sharpness,setSharpness] = useState(50);
  const [contrast,setContrast] = useState(50);

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:4000'); 
    setSocket(ws);

    return () => {
      ws.close();
    };
  }, []);



  return (
     <div className="App">
      <Table className="table-dark">
        <tbody>
          <tr>
            <td className="Attribute">
              <div className='title'>
                <h4>Sound</h4>
              </div>
              <div className="Attribute_Holder">
                <input type="range" min="0" max="100" value={volume} onChange={handleVolumeChange} />
                <p>Current volume: {volume}</p>
              </div>
            </td>
            <td className="Attribute" onClick={handleMuteToggle}>
              <div className='title'>
                <h4>Mute</h4>
              </div>
              <div className="Attribute_Holder">
                <input id="muteCheckbox" type="checkbox" style={{ display: 'none' }} />
                <FontAwesomeIcon icon={mute ? faVolumeMute : faVolumeUp} />
              </div>
            </td>
          </tr>
        </tbody>
      </Table>
    </div>
  )
}

export default PictureControl
