import React, { useState, useEffect } from 'react';
import './SoundControl.css';
import { Table } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faVolumeUp, faVolumeMute } from '@fortawesome/free-solid-svg-icons';

const SoundControl = () => {
  const [volume, setVolume] = useState(50);
  const [mute, setMute] = useState(false);
  const [socket, setSocket] = useState(null);
  const [isRowHovered, setIsRowHovered] = useState(false);

  useEffect(() => {
    const host = '127.0.0.1';
    const port = 5000;
    const client = new WebSocket(`ws://${host}:${port}`);

    client.onopen = () => {
      console.log('Connected');
    };

    client.onmessage = (event) => {
      console.log(`Received: ${event.data}`);

      const receivedData = JSON.parse(event.data);
      if (receivedData.volume) {
        setVolume(receivedData.volume);
      }
      if (receivedData.mute !== undefined) {
        setMute(receivedData.mute);
      }
    };

    client.onerror = (error) => {
      console.log(`Error: ${error.message}`);
    };

    client.onclose = () => {
      console.log('Connection closed');
    };

    setSocket(client);

    return () => {
      client.close();
    };
  }, []);

  const handleVolumeChange = (event) => {
    const newVolume = event.target.value;
    setVolume(newVolume);
    setMute(newVolume === '0' ? true : false);

    if (socket) {
      const message = {
        command: 'setVolume',
        volume: newVolume,
      };
      socket.send(JSON.stringify(message));
    }
  };

  const handleMuteToggle = () => {
    let newMute;
  
    if (volume === 0) {
      newMute = mute;
    } else {
      newMute = !mute;
      console.log(mute)
    }
  
    if (socket) {
      const message = {
        command: 'setMute',
        mute: newMute,
      };
      socket.send(JSON.stringify(message));
    }
  };
  

  
  return (
    <div className="App">
      <Table striped variant="dark">
        <tbody>
          <tr> 
            <td className="RowName">Sound</td>
            <td className="Attribute">
              <input type="range" min="0" max="100" value={volume} onChange={handleVolumeChange} />
              <p>Current volume: {volume}</p>
            </td>
            <td className="RowName">Mute</td>
            <td className="Attribute" onClick={handleMuteToggle}>
              <input
                id="muteCheckbox"
                type="checkbox"
                style={{ display: 'none' }}
              />
              <FontAwesomeIcon icon={mute ? faVolumeMute : faVolumeUp} />
            </td>
          </tr>
        </tbody>
      </Table>
    </div>
  );
};

export default SoundControl;
