import React, { useState, useEffect } from 'react';
import './SoundControl.css';
import { Table } from 'react-bootstrap';

const SoundControl = () => {
  const [volume, setVolume] = useState(50);
  const [mute, setMute] = useState(false);
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    const host = '127.0.0.1';
    const port = 5000;
    const client = new WebSocket(`ws://${host}:${port}`);

    client.onopen = () => {
      console.log('Connected');
    };

    client.onmessage = (event) => {
      console.log(`Received: ${event.data}`);

      const receivedData = JSON.parse(event.data);
      if (receivedData.volume) {
        setVolume(receivedData.volume);
      }
      if (receivedData.mute !== undefined) {
        setMute(receivedData.mute);
      }
    };

    client.onerror = (error) => {
      console.log(`Error: ${error.message}`);
    };

    client.onclose = () => {
      console.log('Connection closed');
    };

    setSocket(client);

    return () => {
      client.close();
    };
  }, []);

  const handleVolumeChange = (event) => {
    const newVolume = event.target.value;
    setVolume(newVolume);

    if (socket) {
      const message = {
        command: 'setVolume',
        volume: newVolume,
      };
      socket.send(JSON.stringify(message));
    }
  };

  const handleMuteToggle = () => {
    const newMute = !mute;
    setMute(newMute);

    if (socket) {
      const message = {
        command: 'setMute',
        mute: newMute,
      };
      socket.send(JSON.stringify(message));
    }
  };

  const users = [
    { name: 'Anil', email: 'anil@test.com', contact: '111' },
    { name: 'Burce', email: 'bruce@test.com', contact: '222' },
    { name: 'Peter', email: 'peter@test.com', contact: '111' },
    { name: 'Sam', email: 'sam@test.com', contact: '777' },
  ]

  return (
    <div>
      <div className="sound-control-container">
        <div className="mute-control">
          <label>Mute:</label>
          <input
            type="checkbox"
            checked={mute}
            onChange={handleMuteToggle}
          />
        </div>
        <div className="volume-control">
                      <label>Volume:</label>
                      <input
                        type="range"
                        min="0"
                        max="100"
                        value={volume}
                        onChange={handleVolumeChange}
                      />
                      <span>{volume}</span>
                    </div>
      </div>
      <div className="row-grid">
        {rows.map((row) => (
          <Table striped variant="dark" >
          <tbody>
          <tr>
            <td>Name</td>
            <td>Email</td>
            <td>Contacts</td>
          </tr>
          {
            users.map((item,i)=>
            item.contact==='111'?
            <tr key={i}>
            <td>{item.name}</td>
            <td>{item.email}</td>
          <td>{item.contact}</td>
          </tr>:null
            )
          }
          </tbody>
        </Table>
        ))}
      </div>
    </div>
  );
};

export default SoundControl;
