import React, { useEffect, useState } from 'react';
import { Table } from 'react-bootstrap';
import './DeviceControl.css';

const SourceControl = () => {
  const [eyeCareEnabled, setEyeCareEnabled] = useState(false);
  const [wakeOnLANEnabled, setWakeOnLANEnabled] = useState(false);
  const [socket, setSocket] = useState(null); 
  const [SourceManagementMenuVisible, setPowerManagementMenuVisible] = useState(false);
  const [selectedSource, setSelectedSource] = useState('Android');

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:4000'); 
    setSocket(ws);

    return () => {
      ws.close();
    };
  }, []);

  const sendEyeCareData = (newEyeCare) => {
    if (socket) {
      const data = {
        type: 'eyecare',
        value: !newEyeCare,
      };
      socket.send(JSON.stringify(data));
    }
  };

  const sendWakeonLanData = (newWakeonLan) => {
    if (socket) {
      const data = {
        type: 'wakeonlan',
        value: !newWakeonLan,
      };
      socket.send(JSON.stringify(data));
    }
  };

  const handleEyeCareToggle = () => {
    setEyeCareEnabled(!eyeCareEnabled);
    sendEyeCareData(eyeCareEnabled)
  };

  const handleWakeOnLANToggle = () => {
    setWakeOnLANEnabled(!wakeOnLANEnabled);
    sendWakeonLanData(wakeOnLANEnabled)
  };

  return (
    <div className="App">
      <Table className="table-dark">
        <tbody>
          <tr>
            <td className="Attribute">
              <div>
                <h4 className='title'>Source</h4>
              </div>
              <div className="Attribute_Holder">
              <h5>{source}</h5>
                <label htmlFor="powerManagementCheckbox" />
                <div className='tableWrapper'>
                {SourceManagementMenuVisible && (
              <Table className="table-dark slide-down-animation">
                <tbody className="Menu">
                  <tr>
                    <td className="visible_menu_wrapper" onClick={() => handleSleepManagementClickMenu('Off')}>
                      <div>
                        <h5 className="title">Off</h5>
                      </div>
                    </td>
                    <td className="visible_menu_wrapper" onClick={() => handleSleepManagementClickMenu('30')}>
                      <div>
                        <h5 className="title">30 Minutes</h5>
                      </div>
                    </td>
                    <td className="visible_menu_wrapper" onClick={() => handleSleepManagementClickMenu('60')}>
                      <div>
                        <h5 className="title">60 Minutes</h5>
                      </div>
                    </td>
                    <td className="visible_menu_wrapper" onClick={() => handleSleepManagementClickMenu('120')}>
                      <div>
                        <h5 className="title">120 Minutes</h5>
                      </div>
                    </td>
                  </tr>
                </tbody>
              </Table>
            )}
            </div>
              </div>
            </td>
            <td className="Attribute" >
              <div>
                <h4 className='title'>IP Address</h4>
              </div>
              <div className="Attribute_Holder">
              <input
                  id="wakeOnLANCheckbox"
                  type="checkbox"
                  className="switch"
                  defaultChecked={wakeOnLANEnabled}
                  onChange={handleWakeOnLANToggle}
                />
                <label htmlFor="wakeOnLANCheckbox" />
              </div>
            </td>
            <td className="Attribute" >
              <div>
                <h4 className='title'>Mac Address</h4>
              </div>
              <div className="Attribute_Holder">
              <input
                  id="wakeOnLANCheckbox"
                  type="checkbox"
                  className="switch"
                  defaultChecked={wakeOnLANEnabled}
                  onChange={handleWakeOnLANToggle}
                />
                <label htmlFor="wakeOnLANCheckbox" />
              </div>
            </td>
            <td className="Attribute" >
              <div>
                <h4 className='title'>Ethernet Status</h4>
              </div>
              <div className="Attribute_Holder">
              <input
                  id="wakeOnLANCheckbox"
                  type="checkbox"
                  className="switch"
                  defaultChecked={wakeOnLANEnabled}
                  onChange={handleWakeOnLANToggle}
                />
                <label htmlFor="wakeOnLANCheckbox" />
              </div>
            </td>
          </tr>
        </tbody>
      </Table>
    </div>
  )
}

export default SourceControl
