import React, { useEffect, useState } from 'react';
import { Table } from 'react-bootstrap';
import './DeviceControl.css';

const DeviceControl = () => {
  const [backLightEnabled, setbackLightEnabled] = useState(false);
  const [wakeOnLANEnabled, setWakeOnLANEnabled] = useState(false);
  const [socket, setSocket] = useState(null);
  const [powerManagementMenuVisible, setPowerManagementMenuVisible] = useState(false);
  const [selectedPowerMode, setSelectedPowerMode] = useState('Stand-by Mode');
  const [selectedSleepTime, setSelectedSleepTime] = useState('Off');

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:4000');
    setSocket(ws);

    return () => {
      ws.close();
    };
  }, []);

  const sendBackLightData = (newBackLight) => {
    if (socket) {
      const data = {
        type: 'backlight',
        value: !newBackLight,
      };
      socket.send(JSON.stringify(data));
    }
  };

  const sendPowerModeData = (mode) => {
    if (socket) {
      const data = {
        type: 'powermode',
        value: mode,
      };
      socket.send(JSON.stringify(data));
    }
  };

  const sendSleepTimeData = (time) => {
    if (socket) {
      const data = {
        type: 'sleeptime',
        value: time,
      };
      socket.send(JSON.stringify(data));
    }
  };

  const handleBackLightToggle = () => {
    const newBackLightEnabled = !backLightEnabled;
    setbackLightEnabled(newBackLightEnabled);
    sendBackLightData(newBackLightEnabled);
  };

  const handlePowerManagementClick = () => {
    setPowerManagementMenuVisible(!powerManagementMenuVisible);
  };

  const handlePowerManagementClickMenu = (mode) => {
    setPowerManagementMenuVisible(false);
    setSelectedPowerMode(mode);
    sendPowerModeData(mode);
  };

  const handleSleepManagementClick = () => {
    setPowerManagementMenuVisible(!powerManagementMenuVisible);
  };

  const handleSleepManagementClickMenu = (time) => {
    setPowerManagementMenuVisible(false);
    setSelectedSleepTime(time);
    sendSleepTimeData(time);
  };

  return (
    <div className="App">
      <Table className="table-dark">
        <tbody>
          <tr>
            <td className="Attribute" onClick={handlePowerManagementClick}>
              <div>
                <h4 className="title">Power Settings</h4>
              </div>
              <div className="Attribute_Holder">
                <h5>{selectedPowerMode}</h5>
                <label htmlFor="powerManagementCheckbox" />
              </div>
            </td>
          </tr>
          {powerManagementMenuVisible && (
            <tr>
              <td className="visible_menu_wrapper" colSpan="3">
                <Table className="table-dark slide-down-animation">
                  <tbody className="Menu">
                    <tr>
                      <td onClick={() => handlePowerManagementClickMenu('Stand-by Mode')}>
                        <div>
                          <h5 className="title">Stand-by Mode</h5>
                        </div>
                      </td>
                      <td onClick={() => handlePowerManagementClickMenu('Last Mode')}>
                        <div>
                          <h5 className="title">Last Mode</h5>
                        </div>
                      </td>
                      <td onClick={() => handlePowerManagementClickMenu('Normal Mode')}>
                        <div>
                          <h5 className="title">Normal Mode</h5>
                        </div>
                      </td>
                    </tr>
                  </tbody>
                </Table>
              </td>
            </tr>
          )}
          <tr>
            <td className="Attribute">
              <div>
                <h4 className="title">Screen Backlight</h4>
              </div>
              <div className="Attribute_Holder">
                <input
                  id="backLightCheckbox"
                  type="checkbox"
                  className="switch"
                  checked={backLightEnabled}
                  onChange={handleBackLightToggle}
                />
                <label htmlFor="backLightCheckbox" />
              </div>
            </td>
            <td className="Attribute" onClick={handleSleepManagementClick}>
              <div>
                <h4 className="title">Sleep Time</h4>
              </div>
              <div className="Attribute_Holder">
                <h5>{selectedSleepTime}</h5>
                <label htmlFor="powerManagementCheckbox" />
              </div>
            </td>
          </tr>
          {powerManagementMenuVisible && (
            <tr>
              <td className="visible_menu_wrapper" colSpan="3">
                <Table className="table-dark slide-down-animation">
                  <tbody className="Menu">
                    <tr>
                      <td onClick={() => handleSleepManagementClickMenu('Off')}>
                        <div>
                          <h5 className="title">Off</h5>
                        </div>
                      </td>
                      <td onClick={() => handleSleepManagementClickMenu('30 Minutes')}>
                        <div>
                          <h5 className="title">30 Minutes</h5>
                        </div>
                      </td>
                      <td onClick={() => handleSleepManagementClickMenu('60 Minutes')}>
                        <div>
                          <h5 className="title">60 Minutes</h5>
                        </div>
                      </td>
                      <td onClick={() => handleSleepManagementClickMenu('120 Minutes')}>
                        <div>
                          <h5 className="title">120 Minutes</h5>
                        </div>
                      </td>
                    </tr>
                  </tbody>
                </Table>
              </td>
            </tr>
          )}
        </tbody>
      </Table>
    </div>
  );
};

export default DeviceControl;
