import React, { useState, useEffect } from 'react';
import { Table } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faVolumeMute, faVolumeUp } from '@fortawesome/free-solid-svg-icons';
import './Configuration.css'


const ConfigurationControl = () => {
  const [volume, setVolume] = useState(50);
  const [mute, setMute] = useState(false);
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:4000');
    setSocket(ws);

    return () => {
      ws.close();
    };
  }, []);

  const handleVolumeChange = (event) => {
    const newVolume = event.target.value;
    setVolume(newVolume);
    setMute(newVolume === '0' ? true : false);
    sendVolumeData(newVolume);
  };

  const handleMuteToggle = () => {
    let newMute;
    if (volume === '0') {
      newMute = mute;
    } else {
      newMute = !mute;
      if (!mute) {
        setVolume('0');
        sendVolumeData('0');
      }
    }
    setMute(newMute);
    sendMuteData(newMute);
  };

  const sendVolumeData = (newVolume) => {
    if (socket) {
      const data = {
        type: 'volume',
        value: newVolume,
      };
      socket.send(JSON.stringify(data));
    }
  };

  const sendMuteData = (newMute) => {
    if (socket) {
      const data = {
        type: 'mute',
        value: newMute,
      };
      socket.send(JSON.stringify(data));
    }
  };

  return (
    <div className="App">
      <Table striped variant="dark">
        <tbody>
          <tr>
            <td className="Attribute">
              <h4>Sound</h4>
              <div className="Attribute_Holder">
                <input type="range" min="0" max="100" value={volume} onChange={handleVolumeChange} />
                <p>Current volume: {volume}</p>
              </div>
            </td>
            <td className="Attribute" onClick={handleMuteToggle}>
              <h4>Mute</h4>
              <div className="Attribute_Holder">
                <input id="muteCheckbox" type="checkbox" style={{ display: 'none' }} />
                <FontAwesomeIcon icon={mute ? faVolumeMute : faVolumeUp} />
              </div>
            </td>
          </tr>
        </tbody>
      </Table>
    </div>
  );
};

export default ConfigurationControl;
