import React, { useEffect, useState } from 'react';
import { Table } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUndoAlt } from '@fortawesome/free-solid-svg-icons';
import { faPowerOff } from '@fortawesome/free-solid-svg-icons';
import './Configuration.css'; // Import the CSS file for custom styling

const SystemControl = () => {
  const [uknownSoruces, setUknownSoruces] = useState(false);
  const [socket, setSocket] = useState(null); 
  const [LanguageManagementMenuVisible, setLanguageManagementMenuVisible] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState('English');

  useEffect(() => {
    const connectWebSocket = async () => {
      const ws = new WebSocket('ws://localhost:4000');
      await new Promise((resolve) => {
        ws.addEventListener('open', resolve);
      });

      setSocket(ws);
    };

    connectWebSocket();

    return () => {
      if (socket) {
        socket.close();
      }
    };
  }, []);

  useEffect(() => {
    const handleSocketMessage = (event) => {
      console.log('Received message:', event.data);
      try {
        const data = JSON.parse(event.data);
        console.log('Parsed data:', data);

        if (data[0].type === 'unknownSources') {
          setUknownSoruces(data[0].value)
        }
        if(data[1].type === 'language')
        {
          setSelectedLanguage(data[1].value)
        }
      } catch (error) {
        // Handle the error here (e.g., display an error message or fallback behavior)
      }
    };

    const sendDirectoryData = () => {
      if (socket && socket.readyState === WebSocket.OPEN) {
        const data = {
          type: 'directory',
          value: 'SystemControl',
        };
        socket.send(JSON.stringify(data));
      }
    };

    if (socket && socket.readyState === WebSocket.OPEN) {
      socket.onmessage = handleSocketMessage;
      sendDirectoryData();
    }
  }, [socket]);

  const sendUnkownSouresData = (newUnkownSources) => {
    if (socket) {
      const data = {
        type: 'unkownsources',
        value: !uknownSoruces,
      };
      socket.send(JSON.stringify(data));
    }
  };

  const handleUnkownSourcesToggle = () => {
    setUknownSoruces(!uknownSoruces);
    sendUnkownSouresData(uknownSoruces)
  };

  const handleLanguageManagementClick = () => {
    setLanguageManagementMenuVisible(!LanguageManagementMenuVisible);
  };

  const handleLanguageManagementClickMenu = (Language) => {
    setLanguageManagementMenuVisible(!LanguageManagementMenuVisible);

    setSelectedLanguage(Language);
  };
  
  return (
    <div className="App">
      <Table className="table-dark">
        <tbody>
          <tr>
            <td className="Attribute">
              <div>
                <h4 className='title'>Set Unknown Sources</h4>
              </div>
              <div className="Attribute_Holder">
                <input
                  id="eyeCareCheckbox"
                  type="checkbox"
                  className="switch"
                  checked={uknownSoruces}
                  onClick={handleUnkownSourcesToggle}
                  readOnly
                />
                <label htmlFor="eyeCareCheckbox" />
              </div>
            </td>
            <td className="Attribute">
              <div>
                <h4 className='title'>Language</h4>
              </div>
              <div className="Attribute_Holder"  onClick={() => handleLanguageManagementClick('')}>
              <h5>{LanguageManagementMenuVisible ? '' : selectedLanguage}</h5>
                <label htmlFor="powerManagementCheckbox" />
                <div className='tableWrapper'>
                {LanguageManagementMenuVisible && (
                <Table className="table-dark slide-down-animation">
                  <tbody className="Menu">
                    <tr>
                      <td className="visible_menu_wrapper" onClick={() => handleLanguageManagementClickMenu('Android')}>
                        <div>
                          <h5 className="title">Android</h5>
                        </div>
                      </td>
                      <td className="visible_menu_wrapper" onClick={() => handleLanguageManagementClickMenu('PC Slot')}>
                        <div>
                          <h5 className="title">PC Slot</h5>
                        </div>
                      </td> 
                      <td className="visible_menu_wrapper" onClick={() => handleLanguageManagementClickMenu('Android Slot')}>
                        <div>
                          <h5 className="title">Android Slot</h5>
                        </div>
                      </td>
                      <td className="visible_menu_wrapper" onClick={() => handleLanguageManagementClickMenu('HDMI1')}>
                        <div>
                          <h5 className="title">HDMI1</h5>
                        </div>
                      </td>
                      <td className="visible_menu_wrapper" onClick={() => handleLanguageManagementClickMenu('VGA1')}>
                        <div>
                          <h5 className="title">VGA1</h5>
                        </div>
                      </td>
                    </tr>
                  </tbody>
                </Table>
              )}
            </div>
              </div>
            </td>
            <td className="Attribute" >
              <div>
                <h4 className='title'>Reboot</h4>
              </div>
              <div className="Attribute_Holder">
                <input
                  id="muteCheckbox"
                  type="checkbox"
                  style={{ display: 'none' }}
                />
                <FontAwesomeIcon icon={faUndoAlt} />
              </div>
            </td>
            <td className="Attribute" >
              <div>
                <h4 className='title'>Power Off</h4>
              </div>
              <div className="Attribute_Holder">
                <input
                  id="muteCheckbox"
                  type="checkbox"
                  style={{ display: 'none' }}
                />
                <FontAwesomeIcon icon={faPowerOff} />
              </div>
            </td>
            
          </tr>
        </tbody>
      </Table>
    </div>  
  )
}

export default SystemControl;
