import React, { useEffect, useState } from 'react';
import { Table } from 'react-bootstrap';
import './Configuration.css'; // Import the CSS file for custom styling

const ConfigurationControl = () => {
  const [eyeCareEnabled, setEyeCareEnabled] = useState(true);
  const [wakeOnLANEnabled, setWakeOnLANEnabled] = useState(true);
  const [socket, setSocket] = useState(null); 

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:4000'); 
    setSocket(ws);

    return () => {
      ws.close();
    };
  }, []);

  const sendEyeCareData = (newEyeCare) => {
    if (socket) {
      const data = {
        type: 'eyecare',
        value: !newEyeCare,
      };
      socket.send(JSON.stringify(data));
    }
  };

  const sendWakeonLanData = (newWakeonLan) => {
    if (socket) {
      const data = {
        type: 'wakeonlan',
        value: !newWakeonLan,
      };
      socket.send(JSON.stringify(data));
    }
  };

  const handleEyeCareToggle = () => {
    setEyeCareEnabled(!eyeCareEnabled);
    sendEyeCareData(eyeCareEnabled)
  };

  const handleWakeOnLANToggle = () => {
    setWakeOnLANEnabled(!wakeOnLANEnabled);
    sendEyeCareData(wakeOnLANEnabled)
  };

  return (
    <div className="App">
      <Table striped variant="dark">
        <tbody>
          <tr>
            <td className="Attribute">
              <div>
                <h4 className='title'>Eye Care</h4>
              </div>
              <div className="Attribute_Holder">
                <input id="eyeCareCheckbox" type="checkbox" className="iphone-switch" checked={eyeCareEnabled} onClick={handleEyeCareToggle} />
                <label htmlFor="eyeCareCheckbox" />
              </div>
            </td>
            <td className="Attribute" >
              <div>
                <h4 className='title'>Wake-on-LAN</h4>
              </div>
              <div className="Attribute_Holder">
                <input id="wakeOnLANCheckbox" type="checkbox" className="iphone-switch" checked={wakeOnLANEnabled} onClick={handleWakeOnLANToggle} />
                <label htmlFor="wakeOnLANCheckbox" />
              </div>
            </td>
          </tr>
        </tbody>
      </Table>
    </div>
  );
};

export default ConfigurationControl;
