import React, { useEffect, useState } from 'react';
import { Table } from 'react-bootstrap';
import './Configuration.css'; // Import the CSS file for custom styling

const NetworkControl = () => {
  const [eyeCareEnabled, setEyeCareEnabled] = useState(false);
  const [wakeOnLANEnabled, setWakeOnLANEnabled] = useState(false);
  const [socket, setSocket] = useState(null); 

  useEffect(() => {
    const connectWebSocket = async () => {
      const ws = new WebSocket('ws://localhost:4000');
      await new Promise((resolve) => {
        ws.addEventListener('open', resolve);
      });

      setSocket(ws);
    };

    connectWebSocket();

    return () => {
      if (socket) {
        socket.close();
      }
    };
  }, []);


  useEffect(() => {
    const handleSocketMessage = (event) => {
      console.log('Received message:', event.data);
      try {
        const data = JSON.parse(event.data);
        console.log('Parsed data:', data);
        
        if (data[0].type === 'powermode') {  
          setSelectedMode(data[0].value)
          
        } 
        if (data[1].type === 'backlight') {
          setBackLightEnabled(data[1].value)
        }
        if(data[2].type === 'sleep')
        {
          setSleepMode(data[2].value)
        }

      } catch (error) {

        // Handle the error here (e.g., display an error message or fallback behavior)
      }
    };

    const sendDirectoryData = () => {
      if (socket && socket.readyState === WebSocket.OPEN) {
        const data = {
          type: 'directory',
          value: 'NetworkControl',
        };
        socket.send(JSON.stringify(data));
      }
    };

    if (socket && socket.readyState === WebSocket.OPEN) {
      socket.onmessage = handleSocketMessage;
      sendDirectoryData();
    }
  }, [socket]);

  const sendEyeCareData = (newEyeCare) => {
    if (socket) {
      const data = {
        type: 'eyecare',
        value: !newEyeCare,
      };
      socket.send(JSON.stringify(data));
    }
  };

  const sendWakeonLanData = (newWakeonLan) => {
    if (socket) {
      const data = {
        type: 'wakeonlan',
        value: !newWakeonLan,
      };
      socket.send(JSON.stringify(data));
    }
  };

  const handleEyeCareToggle = () => {
    setEyeCareEnabled(!eyeCareEnabled);
    sendEyeCareData(eyeCareEnabled)
  };

  const handleWakeOnLANToggle = () => {
    setWakeOnLANEnabled(!wakeOnLANEnabled);
    sendWakeonLanData(wakeOnLANEnabled)
  };

  return (
    <div className="App">
      <Table className="table-dark">
        <tbody>
          <tr>
            <td className="Attribute">
              <div>
                <h4 className='title'>Network Status</h4>
              </div>
              <div className="Attribute_Holder">
              <input
                  id="eyeCareCheckbox"
                  type="checkbox"
                  className="switch"
                  defaultChecked={eyeCareEnabled}
                  onChange={handleEyeCareToggle}
                />
                <label htmlFor="eyeCareCheckbox" />
              </div>
            </td>
            <td className="Attribute" >
              <div>
                <h4 className='title'>IP Address</h4>
              </div>
              <div className="Attribute_Holder">
              <input
                  id="wakeOnLANCheckbox"
                  type="checkbox"
                  className="switch"
                  defaultChecked={wakeOnLANEnabled}
                  onChange={handleWakeOnLANToggle}
                />
                <label htmlFor="wakeOnLANCheckbox" />
              </div>
            </td>
            <td className="Attribute" >
              <div>
                <h4 className='title'>Mac Address</h4>
              </div>
              <div className="Attribute_Holder">
              <input
                  id="wakeOnLANCheckbox"
                  type="checkbox"
                  className="switch"
                  defaultChecked={wakeOnLANEnabled}
                  onChange={handleWakeOnLANToggle}
                />
                <label htmlFor="wakeOnLANCheckbox" />
              </div>
            </td>
            <td className="Attribute" >
              <div>
                <h4 className='title'>Ethernet Status</h4>
              </div>
              <div className="Attribute_Holder">
              <input
                  id="wakeOnLANCheckbox"
                  type="checkbox"
                  className="switch"
                  defaultChecked={wakeOnLANEnabled}
                  onChange={handleWakeOnLANToggle}
                />
                <label htmlFor="wakeOnLANCheckbox" />
              </div>
            </td>
          </tr>
        </tbody>
      </Table>
    </div>
  )
}

export default NetworkControl
