import React, { useEffect, useState } from 'react';
import { Table } from 'react-bootstrap';
import './DeviceControl.css';

const TimerControl = () => {
  const [eyeCareEnabled, setEyeCareEnabled] = useState(false);
  const [wakeOnLANEnabled, setWakeOnLANEnabled] = useState(false);
  const [socket, setSocket] = useState(null);
  const [currentTime, setCurrentTime] = useState('');
  const [alarmTime, setAlarmTime] = useState('');

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:4000');
    setSocket(ws);

    return () => {
      ws.close();
    };
  }, []);

  useEffect(() => {
    // Update the current time every second
    const interval = setInterval(() => {
      const time = new Date().toLocaleTimeString();
      setCurrentTime(time);
    }, 1000);

    return () => {
      clearInterval(interval);
    };
  }, []);

  const handleAlarmInputChange = (event) => {
    setAlarmTime(event.target.value);
  };

  const handleSetAlarm = () => {
    // Perform actions based on the alarm time
    console.log('Alarm set for:', alarmTime);
  };

  return (
    <div className="App">
      <Table className="table-dark">
        <tbody>
          <tr>
            <td className="Attribute">
              <div>
                <h4 className="title">Alarm</h4>
              </div>
              <div className="Attribute_Holder">
                <input
                  type="time"
                  value={alarmTime}
                  onChange={handleAlarmInputChange}
                />
                <button onClick={handleSetAlarm}>Set Alarm</button>
              </div>
            </td>
          </tr>
        </tbody>
      </Table>

      <div>
        <h4>Current Time: {currentTime}</h4>
      </div>
    </div>
  );
};

export default TimerControl;
