import React, { useState } from 'react';
import { Table } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faVolumeMute, faVolumeUp } from '@fortawesome/free-solid-svg-icons';
import './Configuration.css'; // Import the CSS file for custom styling

const ConfigurationControl = () => {
  const [volume, setVolume] = useState(50);
  const [mute, setMute] = useState(false);
  const [eyeCareEnabled, setEyeCareEnabled] = useState(true);
  const [wakeOnLANEnabled, setWakeOnLANEnabled] = useState(true);

  const handleVolumeChange = (event) => {
    const newVolume = event.target.value;
    setVolume(newVolume);
    setMute(newVolume === '0' ? true : false);
    sendVolumeData(newVolume);
  };

  const handleMuteToggle = () => {
    let newMute;
    if (volume === '0') {
      newMute = mute;
    } else {
      newMute = !mute;
      if (!mute) {
        setVolume('0');
        sendVolumeData('0');
      }
    }
    setMute(newMute);
    sendMuteData(newMute);
  };

  const handleEyeCareToggle = () => {
    setEyeCareEnabled(!eyeCareEnabled);
    // Send eye care enabled/disabled data to the server or perform any necessary action
  };

  const handleWakeOnLANToggle = () => {
    setWakeOnLANEnabled(!wakeOnLANEnabled);
    // Send Wake-on-LAN enabled/disabled data to the server or perform any necessary action
  };

  const sendVolumeData = (newVolume) => {
    // Send volume data to the server or perform any necessary action
  };

  const sendMuteData = (newMute) => {
    // Send mute data to the server or perform any necessary action
  };

  return (
    <div className="App">
      <Table striped variant="dark">
        <tbody>
          <tr>
            <td className="Attribute" onClick={handleEyeCareToggle}>
              <div>
                <h4 className='title'>Eye Care</h4>
              </div>
              <div className="Attribute_Holder">
                <input id="eyeCareCheckbox" type="checkbox" className="iphone-switch" checked={eyeCareEnabled} readOnly />
                <label htmlFor="eyeCareCheckbox" />
              </div>
            </td>
            <td className="Attribute" onClick={handleWakeOnLANToggle}>
              <h4>Wake-on-LAN</h4>
              <div className="Attribute_Holder">
                <input id="wakeOnLANCheckbox" type="checkbox" className="iphone-switch" checked={wakeOnLANEnabled} readOnly />
                <label htmlFor="wakeOnLANCheckbox" />
              </div>
            </td>
          </tr>
        </tbody>
      </Table>
    </div>
  );
};

export default ConfigurationControl;
