import React, { useState, useEffect } from 'react';
import './SoundControl.css';
import 'bootstrap/dist/css/bootstrap.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faVolumeUp } from '@fortawesome/free-solid-svg-icons';
import { faVolumeMute } from '@fortawesome/free-solid-svg-icons';

const SoundControl = () => {
  const [volume, setVolume] = useState(50);
  const [mute, setMute] = useState(false);
  const [websocket, setWebsocket] = useState(null);

  useEffect(() => {
    const socket = new WebSocket('ws://localhost:8080'); // Replace with your WebSocket server URL
    setWebsocket(socket);

    return () => {
      socket.close();
    };
  }, []);

  useEffect(() => {
    if (websocket) {
      websocket.onmessage = (event) => {
        const receivedData = JSON.parse(event.data);
        // Handle received data from the WebSocket server
        // Example: update volume or mute state based on received data
        if (receivedData.volume) {
          setVolume(receivedData.volume);
        }
        if (receivedData.mute !== undefined) {
          setMute(receivedData.mute);
        }
      };
    }
  }, [websocket]);

  const handleVolumeChange = (event) => {
    const newVolume = event.target.value;
    setVolume(newVolume);

    if (websocket) {
      websocket.send(JSON.stringify({ volume: newVolume }));
    }
  };

  const handleMuteToggle = () => {
    const newMute = !mute;
    setMute(newMute);

    if (websocket) {
      websocket.send(JSON.stringify({ mute: newMute }));
    }
  };

  return (
    <div className="row no-gutters">
      <div className="col-md-12">
        <div className="card">
          <div className="card-header">Volume</div>
          <div className="card-body">
            <input type="range" min="0" max="100" value={volume} onChange={handleVolumeChange} />
            <p>Current volume: {volume}</p>
          </div>
        </div>
      </div>
      <div className="col-md-12">
        <div className="card">
          <div className="card-header">Mute</div>
          <div className="card-body">
            <label htmlFor="muteCheckbox" className={`mute-checkbox ${mute ? 'crossed' : ''}`}>
              <input
                id="muteCheckbox"
                type="checkbox"
                checked={mute}
                onChange={handleMuteToggle}
                style={{ display: 'none' }}
              />
              <FontAwesomeIcon icon={mute ? faVolumeMute : faVolumeUp} />
            </label>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SoundControl;
