import React, { useEffect, useState } from 'react';
import { Table } from 'react-bootstrap';
import './DeviceControl.css';

const DeviceControl = () => {
  const [backLightEnabled, setbackLightEnabled] = useState(false);
  const [wakeOnLANEnabled, setWakeOnLANEnabled] = useState(false);
  const [socket, setSocket] = useState(null);
  const [powerManagementMenuVisible, setPowerManagementMenuVisible] = useState(false);
  const [selectedMode, setSelectedMode] = useState('');

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:4000');
    setSocket(ws);

    return () => {
      ws.close();
    };
  }, []);

  const sendBackLightData = (newBackLight) => {
    if (socket) {
      const data = {
        type: 'backlight',
        value: !newBackLight,
      };
      socket.send(JSON.stringify(data));
    }
  };

  const sendWakeOnLANData = (newWakeOnLAN) => {
    if (socket) {
      const data = {
        type: 'wakeonlan',
        value: !newWakeOnLAN,
      };
      socket.send(JSON.stringify(data));
    }
  };

  const handleBackLightToggle = () => {
    const newBackLightEnabled = !backLightEnabled;
    setbackLightEnabled(newBackLightEnabled);
    sendBackLightData(backLightEnabled);
  };

  const handleWakeOnLANToggle = () => {
    const newWakeOnLANEnabled = !wakeOnLANEnabled;
    setWakeOnLANEnabled(newWakeOnLANEnabled);
    sendWakeOnLANData(newWakeOnLANEnabled);
  };

  const handlePowerManagementClick = (mode) => {
    setPowerManagementMenuVisible(!powerManagementMenuVisible);
    setSelectedMode(mode);
  };

  return (
    <div className="App">
      <Table className="table-dark">
        <tbody>
          <tr>
            <td className="Attribute" onClick={() => handlePowerManagementClick('')}>
              <div>
                <h4 className='title'>Power Settings</h4>
              </div>
              <div className="Attribute_Holder">
                <h5>{selectedMode}</h5>
                <label htmlFor="powerManagementCheckbox" />
              </div>
            </td>
          </tr>
          {powerManagementMenuVisible && (
            <Table className="table-dark slide-down-animation">
              <tbody className="Menu">
                <tr>
                  <td onClick={() => handlePowerManagementClick('Stand-by Mode')}>
                    <div className='visible_menu_wrapper'>
                      <h4 className='title'>Stand-by Mode</h4>
                    </div>
                  </td>
                  <td onClick={() => handlePowerManagementClick('Last Mode')}>
                    <div className='visible_menu_wrapper'>
                      <h4 className='title'>Last Mode</h4>
                    </div>
                  </td>
                  <td onClick={() => handlePowerManagementClick('Normal Mode')}>
                    <div className='visible_menu_wrapper'>
                      <h4 className='title'>Normal Mode</h4>
                    </div>
                  </td>
                </tr>
              </tbody>
            </Table>
          )}
          <tr>
            <td className="Attribute">
              <div>
                <h4 className='title'>Screen Backlight</h4>
              </div>
              <div className="Attribute_Holder">
                <input
                  id="backLightCheckbox"
                  type="checkbox"
                  className="switch"
                  checked={backLightEnabled}
                  onChange={handleBackLightToggle}
                />
                <label htmlFor="backLightCheckbox" />
              </div>
            </td>
            <td className="Attribute" >
              <div>
                <h4 className='title'>Wake-on-LAN</h4>
              </div>
              <div className="Attribute_Holder">
                <input
                  id="wakeOnLANCheckbox"
                  type="checkbox"
                  className="switch"
                  checked={wakeOnLANEnabled}
                  onChange={handleWakeOnLANToggle}
                />
                <label htmlFor="wakeOnLANCheckbox" />
              </div>
            </td>
          </tr>
        </tbody>
      </Table>
    </div>
  );
}

export default DeviceControl;
